import { Auth } from 'aws-amplify'
import { fetchAuthUser } from '../actions/index'

//used by Register screen
export function authSignUp( username, password, preferred_username, picture, navigation ) {
  Auth.signUp({ username, password, attributes: { preferred_username, picture } })
    .then( navigation.navigate( 'ConfirmSignUp' ))
    .catch( err => console.log( '❌ Error Signing Up' , err ))
}

//used by UserDetails screen
export function updatePreferred_username( payload, dispatch ) { 
  Auth.currentAuthenticatedUser()
    .then( user => { Auth.updateUserAttributes( user, { "preferred_username": payload })}) 
    .then( dispatch( fetchAuthUser() ) )
    .catch( err => console.log( '❌ Error Updating Details', err ))
}

//used by UserDetails screen
export function updatePassword( payload, dispatch ) { 
  Auth.currentAuthenticatedUser()
    .then( user => { Auth.chnage( user, { "preferred_username": payload })}) 
    // .then( dispatch( fetchAuthUser() ) )
    .catch( err => console.log( '❌ Error Updating Details', err ))
}
