import React from "react"
import { StyleSheet, View, FlatList, Image } from "react-native"
import { connect } from "react-redux"
import Amplify, { Storage } from 'aws-amplify'

import ListItemSeparator from "../../componets/ListItemSeparator"
import colors from "../../config/colors"
import Icon from "../../componets/Icon"
import Screen from "../../componets/Screen"
import UserSnip from "../../componets/userSnip"

const menuItems = [
  {
    title: "My Collection",
    icon: {
      name: "format-list-bulleted",
      backgroundColor: colors.secondary,
    },
    targetScreen: 'Home',
  },
  {
    title: "My Details",
    icon: {
      name: "email",
      backgroundColor: colors.secondary,
    },
    targetScreen: 'Home',
  },
  {
    title: "Log Out",
    icon: {
      name: "logout",
      backgroundColor: colors.secondary,
    },
    targetScreen: 'Logout',
  },
]





function Profile({ navigation, user }) {


 async function userPic() {
  try {
    const userProfileUrl = await Storage.get('User-Profile-Pictures/default-picture.png', { expires: 60 })
    console.log(userProfileUrl)
    return userProfileUrl
  } catch (error) {
    console.error('error accessing the user image',err)
  }
 }


  return (
    <Screen style={styles.screen}>
      <View style={styles.container}>
        <UserSnip
          title={user.username}
          subTitle="Sneaker Count 5"
          image={userPic()}
        />
      </View>
      <View style={styles.container}>
        <FlatList
          data={menuItems}
          keyExtractor={(menuItem) => menuItem.title}
          ItemSeparatorComponent={ListItemSeparator}
          renderItem={({ item }) => (
            <UserSnip
              title={item.title}
              IconComponent={
                <Icon
                  name={item.icon.name}
                  backgroundColor={item.icon.backgroundColor}
                />
              }
              onPress={() => {navigation.navigate(item.targetScreen)}}
            />
          )}
        />
      </View>
    </Screen>
  )
}

function mapStateToProps (globalState) {
  return {
    user:globalState.user
  }
}

export default connect(mapStateToProps)(Profile)

const styles = StyleSheet.create({
  screen: {
    backgroundColor: colors.white,
  },
  container: {
    marginVertical: 20,
  },
})
