import React, {useState} from "react"
import { StyleSheet } from "react-native"
import { connect } from "react-redux"

import Screen from "../../componets/Screen"
import AppPicker from "../../componets/AppPicker"

const Brands = []

function Search(props) {

    for(let i = 0; i < props.sneakers.length; i++){
        Brands.push(props.sneakers[i]['brand'])
    }

    // setup state to capture username email and password from inputs
  const [brand, setBrand] = useState('')
  const [model, setModel] = useState('')
  const [submodel, setSubmodel ] = useState('')

  return (
    <Screen style={styles.container}>
        <AppPicker items={Brands} name="brand" placeholder="Brand" onChangeText={text => setBrand(text)}/>
        <AppPicker items={Brands} name="model" placeholder="Model" onChangeText={text => setModel(text)}/>
        <AppPicker items={Brands} name="submodel" placeholder="SubModel" onChangeText={text => setSubmodel(text)} />
        {/* <SubmitButton title="Search" /> */}
    </Screen>
  )
}

const styles = StyleSheet.create({
  container: {
    padding: 10,
  },
})

function mapStateToProps (globalState) {
    return {
      sneakers: globalState.sneakers
    }
}

export default connect(mapStateToProps)(Search)
