import React from "react"
import { StyleSheet, View, FlatList } from "react-native"
import { connect } from "react-redux"
import { Auth } from 'aws-amplify'

import ListItemSeparator from "../../componets/ListItemSeparator"
import colors from "../../config/colors"
import Icon from "../../componets/Icon"
import Screen from "../../componets/Screen"
import UserSnip from "../../componets/userSnip"
import AppButton from "../../componets/AppButton"

const menuItems = [
  {
    title: "My Listings",
    icon: {
      name: "format-list-bulleted",
      backgroundColor: colors.primary,
    },
    press: 'Home',
  },
  {
    title: "My Messages",
    icon: {
      name: "email",
      backgroundColor: colors.secondary,
    },
    press: navigation,
  },
]




function Profile({ navigation, user, updateAuthState }) {

  async function signOut() {
    try {
      await Auth.signOut()
        updateAuthState('loggedOut')
    } catch (error) {
        console.log('Error signing out: ', error)
    }
  }



  return (
    <Screen style={styles.screen}>
      <View style={styles.container}>
        <UserSnip
          title={user.username}
          subTitle="Sneaker Count 5"
          image={require("../../../assets/user1.png")}
        />
      </View>
      <View style={styles.container}>
        <FlatList
          data={menuItems}
          keyExtractor={(menuItem) => menuItem.title}
          ItemSeparatorComponent={ListItemSeparator}
          renderItem={({ item }) => (
            <UserSnip
              title={item.title}
              IconComponent={
                <Icon
                  name={item.icon.name}
                  backgroundColor={item.icon.backgroundColor}
                />
              }
              onPress={() => {}}
            />
          )}
        />
      </View>
      <AppButton title="Logout" onPress={signOut} />
      <UserSnip
        title="Log Out"
        IconComponent={<Icon name="logout" backgroundColor="#ffe66d" />}
      />
    </Screen>
  )
}

function mapStateToProps (globalState) {
  return {
    user:globalState.user
  }
}

export default connect(mapStateToProps)(Profile)

const styles = StyleSheet.create({
  screen: {
    backgroundColor: colors.light,
  },
  container: {
    marginVertical: 20,
  },
})
