import { SET_USER , UPDATE_USER } from '../actions'

const initialState = []

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_USER:
      return action.user
    case UPDATE_USER:
        return [...state]
    default:
      return state
  }
}

export default reducer
