import React from "react"
import { StyleSheet, Image } from "react-native"
import { connect } from "react-redux"
import Amplify, { Storage } from 'aws-amplify'
import { v4 as uuidv4 } from 'uuid'

import colors from "../../config/colors"
import Screen from "../../componets/Screen"
import ImageInputList from "../../componets/ImageInputList"



function UserDetails({ navigation, user }) {

  // const [picture, setPicture] = useState('')

  const uploadToStorage = async pathToImageFile => {
    try {
      const response = await fetch(pathToImageFile)
      console.log(response)
      const blob = await response.blob()
      console.log(blob)
      Storage.put(`${uuidv4()}.jpeg`, 'Protected Content', blob, {
        level: 'protected',
        contentType: 'image/jpeg',
      })
    } catch (err) {
      console.log(err)
    }
  }


 async function userPic() {
  try {
    const userProfileUrl = await Storage.get('default-picture.png')
    console.log(userProfileUrl)
    return userProfileUrl
  } catch (error) {
    console.error('error accessing the user image',err)
  }
 }



const handleAdd = (uri) => {
  uploadToStorage(uri)
  console.log(uri)
}

// const handleRemove = (uri) => {
//   setFieldValue(
//     name,
//     imageUris.filter((imageUri) => imageUri !== uri)
//   )
// }

return (
  <Screen>
    {/* <Image
    style={styles.logo}
    options="{'level':'private'}"
    source={{
      uri:user.picture
    }}
    />   */}
  <ImageInputList
      imageUris={[user.picture]}
      onAddImage={handleAdd}
      // onRemoveImage={handleRemove}
    />
    {/* <ErrorMessage error={errors[name]} visible={touched[name]} /> */}
  </Screen>
)
}

function mapStateToProps (globalState) {
  return {
    user:globalState.user
  }
}

export default connect(mapStateToProps)(UserDetails)

const styles = StyleSheet.create({
  screen: {
    backgroundColor: colors.white,
  },
  container: {
    marginVertical: 20,
  },
  logo: {
    width: 66,
    height: 58,
  },
})
