import React, {useState} from "react"
import { StyleSheet, Image, View } from "react-native"
import { connect } from "react-redux"
import { Storage } from 'aws-amplify'

import colors from "../../config/colors"
import Screen from "../../componets/Screen"
import ImageInput from "../../componets/ImageInput"
import ProfileSnip from "../../componets/ProfileSnip"

const menuItems = [
  {
    title: "Email",
    icon: {
      name: "format-list-bulleted",
      backgroundColor: colors.secondary,
    },
    targetScreen: 'Email',
  },
  {
    title: "My Details",
    icon: {
      name: "mdi-email",
      backgroundColor: colors.secondary,
    },
    targetScreen: 'UserDetails',
  },
  {
    title: "Log Out",
    icon: {
      name: "logout",
      backgroundColor: colors.secondary,
    },
    targetScreen: 'Logout',
  },
]

function UserDetails({ navigation, user }) {

  Storage.configure({ level: 'protected' })

  Storage.list('profile-pics/', { level: 'protected' })
  .then(result => console.log(result))
  .catch(err => console.log(err))

  const [picture, setPicture] = useState(user.picture)

  const uploadToStorage = async pathToImageFile => {
    try {
      const response = await fetch(pathToImageFile)
      console.log(response)
      const blob = await response.blob()
      console.log(blob)
      Storage.put(`profile-pics/til.jpeg`, blob, {
        contentType: 'image/jpeg',
      })
    } catch (err) {
      console.log(err)
    }
  }

  const getfromStorage = async () => {
    try {
      const response = await Storage.get('profile-pics/til.jpeg')
      setPicture(response)
      }
    catch (err) {
      console.error('error accessing the user image',err)
    }
  }

  getfromStorage()
  console.log(picture)

const handleAdd = (uri) => {
  uploadToStorage(uri)

}

