    try {
      const response = await fetch(pathToImageFile)
      console.log(response)
      const blob = await response.blob()
      console.log(blob)
      Storage.put(`test2.png`, 'Protected Content', blob, {
        level: 'protected',
        contentType: 'image/png',
      })
    } catch (err) {
      console.log(err)
    }
  }


 async function userPic() {
