import React, {useState} from "react"
import { StyleSheet, View} from "react-native"
import { connect } from "react-redux"
import { Auth, Storage } from 'aws-amplify'

import colors from "../../config/colors"
import Screen from "../../componets/Screen"
import ProfileImageInput from "../../componets/ProfileImageInput"
import ProfileSnip from "../../componets/ProfileSnip"
import AppText from "../../componets/AppText"

function Editpic({ navigation, user }) {

    let authUser = Auth.currentAuthenticatedUser()

    Storage.configure({ level: 'protected' })

    const [picture, setPicture] = useState(user.picture)

    const uploadToStorage = async pathToImageFile => {
        try {
        const response = await fetch(pathToImageFile)
        const blob = await response.blob()
        Storage.put(`profile-pics/profile-picture.jpeg`, blob, {
            contentType: 'image/jpeg',
        })
        .then(getfromStorage())
        } 
        catch (err) {
            console.log(err)
    }
  }

  const getfromStorage = async () => {
    try {
      const response = await Storage.get('profile-pics/profile-picture.jpeg')
      .then(Auth.updateUserAttributes(authUser, {
        'picture': response,
         }))
      }
    catch (err) {
      console.error('error accessing the user image',err)
    }
  }

  const updateAuth = async (imageLocation) => {
    try {
        Auth.updateUserAttributes(authUser, {
          'picture': imageLocation,
           })
        }
      catch (err) {
        console.error('error updating ',err)
      }
  }


const handleAdd = (uri) => {
    uploadToStorage(uri)

}

return (
  <Screen>
    <View style={styles.container}>
        <ProfileSnip
          title={user.username}
          image={user.picture}
        />
      </View>
    <View style={styles.container}>
        <AppText>Click the camera Icon below to upload new profile picture</AppText>
        <ProfileImageInput 
        imageUri={picture}
        onChangeImage={(uri) => handleAdd(uri)}
        /> 
    </View>

  </Screen>
)
}

function mapStateToProps (globalState) {
  return {
    user:globalState.user
  }
}

export default connect(mapStateToProps)(Editpic)

const styles = StyleSheet.create({
  screen: {
    backgroundColor: colors.white,
  },
  container: {
    marginVertical: 20,
  },
  logo: {
    width: 66,
    height: 58,
  },
})
